server/api/airquality.py.content:
from fastapi import APIRouter, HTTPException, Query
from typing import Optional, Dict, List, Any
import asyncio
from datetime import datetime, timezone

from services.tempo_service import TempoService
from services.openaq_service import OpenAQService
from services.weather_service import WeatherService
from services.ml_service import MLService
from services.cache_service import CacheService
from utils.aqi_calculator import calculate_aqi, get_health_advice
from utils.geo_utils import format_location_name
from models.air_quality import (
    AirQualityResponse, 
    WeatherData, 
    AQIData, 
    ForecastPoint, 
    HealthAdvice, 
    DataProvenance,
    PollutantData
)

router = APIRouter()

# Initialize services
tempo_service = TempoService()
openaq_service = OpenAQService()
weather_service = WeatherService()
ml_service = MLService()
cache_service = CacheService()

@router.get("/airquality", response_model=AirQualityResponse)
async def get_air_quality(
    lat: float = Query(..., ge=-90, le=90, description="Latitude"),
    lon: float = Query(..., ge=-180, le=180, description="Longitude"),
    hours: int = Query(24, ge=1, le=48, description="Forecast hours")
):
    """
    Get comprehensive air quality data for a location including:
    - Current pollutant levels from TEMPO and OpenAQ
    - Weather conditions from IMERG/MERRA-2
    - AQI calculation and health advice
    - ML-based forecast
    - Historical data
    """
    try:
        # Check cache first
        cache_key = f"airquality:{lat:.2f}:{lon:.2f}"
        cached_data = cache_service.get(cache_key)
        if cached_data:
            return cached_data

        # Fetch data from all sources concurrently
        tasks = [
            tempo_service.get_pollutants(lat, lon),
            openaq_service.get_nearest_measurements(lat, lon),
            weather_service.get_weather_data(lat, lon),
            openaq_service.get_historical_data(lat, lon, days=7)
        ]

        tempo_data, openaq_data, weather_data, historical_data = await asyncio.gather(
            *tasks, return_exceptions=True
        )

        # Handle exceptions from individual services
        tempo_dict: Dict[str, Any] = {}
        if isinstance(tempo_data, Exception):
            tempo_dict = {}
        elif tempo_data:
            tempo_dict = tempo_data

        openaq_dict: Dict[str, Any] = {}
        if isinstance(openaq_data, Exception):
            openaq_dict = {}
        elif openaq_data:
            openaq_dict = openaq_data

        weather_dict: Dict[str, Any] = {}
        if isinstance(weather_data, Exception):
            weather_dict = {}
        elif weather_data:
            weather_dict = weather_data

        historical_list: List[Dict[str, Any]] = []
        if isinstance(historical_data, Exception):
            historical_list = []
        elif historical_data:
            historical_list = historical_data

        # Merge pollutant data from TEMPO and OpenAQ
        merged_pollutants: Dict[str, Dict[str, Any]] = {}

        # TEMPO data (satellite)
        for pollutant, value in tempo_dict.items():
            if value is not None:
                merged_pollutants[pollutant] = {
                    "value": value,
                    "unit": "¬µg/m¬≥" if pollutant != "hcho" else "ppb",
                    "source": ["TEMPO"]
                }

        # OpenAQ data (ground stations) - merge or use as fallback
        for pollutant, value in openaq_dict.get("pollutants", {}).items():
            if pollutant in merged_pollutants:
                # Average TEMPO and OpenAQ values, weighted towards ground truth
                tempo_val = merged_pollutants[pollutant]["value"]
                merged_val = (tempo_val * 0.4) + (value * 0.6)  # Weight ground stations higher
                merged_pollutants[pollutant]["value"] = merged_val
                merged_pollutants[pollutant]["source"].append("OpenAQ")
            else:
                merged_pollutants[pollutant] = {
                    "value": value,
                    "unit": "¬µg/m¬≥",
                    "source": ["OpenAQ"]
                }

        # Calculate AQI from pollutants
        aqi_value = calculate_aqi(merged_pollutants)

        # Generate health advice
        health_advice = get_health_advice(aqi_value, weather_dict)

        # Generate forecast using ML
        forecast_data = await ml_service.predict_forecast(
            lat, lon, historical_list, weather_dict, hours
        )

        # Build response
        # Convert pollutants to PollutantData models
        pollutant_models = {
            key: PollutantData(**value) 
            for key, value in merged_pollutants.items()
        }

        response_data = AirQualityResponse(
            coord={"lat": lat, "lon": lon},
            location_name=format_location_name(lat, lon),
            timestamp_iso=datetime.now(timezone.utc).isoformat(),
            pollutants=pollutant_models,
            weather=WeatherData(**weather_dict) if weather_dict else WeatherData(
                precip_mm=0.0, wind_speed_ms=0.0, temp_c=20.0, humidity=60, source=[]
            ),
            aqi=AQIData(
                value=aqi_value,
                scale="0-500",
                category=get_aqi_category(aqi_value)
            ),
            forecast_24h=[ForecastPoint(**point) for point in forecast_data],
            advice=HealthAdvice(**health_advice),
            history_7d=[ForecastPoint(**point) for point in historical_list[-168:]] if historical_list else [],
            provenance=DataProvenance(
                tempo={"product": "NO2/O3/PM/HCHO", "nrt": True} if tempo_dict else None,
                openaq={"station_ids": openaq_dict.get("station_ids", [])} if openaq_dict else None,
                meteo={
                    "imerg": bool(weather_dict.get("precip_mm") is not None),
                    "merra2": bool(weather_dict.get("wind_speed_ms") is not None)
                }
            )
        )

        # Cache the response
        cache_service.set(cache_key, response_data)

        return response_data

    except Exception as e:
        print(f"Error in air quality endpoint: {e}")
        raise HTTPException(status_code=500, detail=f"Failed to fetch air quality data: {str(e)}")

def get_aqi_category(aqi: int) -> str:
    """Convert AQI value to category string"""
    if aqi <= 50:
        return "Good"
    elif aqi <= 100:
        return "Moderate"
    elif aqi <= 150:
        return "Unhealthy for Sensitive Groups"
    elif aqi <= 200:
        return "Unhealthy"
    elif aqi <= 300:
        return "Very Unhealthy"
    else:
        return "Hazardous"

server/services/openaq_service.py.content:
import httpx
import asyncio
from typing import Dict, List, Optional
from datetime import datetime, timedelta
from config import get_settings

settings = get_settings()

class OpenAQService:
    """
    OpenAQ air quality data service
    Retrieves real-time and historical air quality measurements from ground stations
    """

    def __init__(self):
        self.base_url = settings.openaq_base_url
        self.timeout = 15.0

    async def get_nearest_measurements(self, lat: float, lon: float, radius_km: int = 50) -> Dict:
        """
        Get latest measurements from nearest ground stations
        """
        try:
            async with httpx.AsyncClient(timeout=self.timeout) as client:
                # Find nearest stations
                stations_response = await client.get(
                    f"{self.base_url}/locations",
                    params={
                        "coordinates": f"{lat},{lon}",
                        "radius": radius_km * 1000,  # Convert to meters
                        "limit": 10,
                        "order_by": "distance"
                    }
                )
                stations_response.raise_for_status()
                stations_data = stations_response.json()

                if not stations_data.get("results"):
                    return {}

                # Get measurements from nearest stations
                station_ids = [station["id"] for station in stations_data["results"][:5]]

                measurements_response = await client.get(
                    f"{self.base_url}/measurements",
                    params={
                        "location_id": ",".join(map(str, station_ids)),
                        "limit": 100,
                        "order_by": "datetime",
                        "sort": "desc",
                        "date_from": (datetime.now() - timedelta(hours=6)).isoformat(),
                    }
                )
                measurements_response.raise_for_status()
                measurements_data = measurements_response.json()

                # Process measurements
                pollutants = {}
                station_ids_used = set()

                for measurement in measurements_data.get("results", []):
                    parameter = measurement.get("parameter")
                    value = measurement.get("value")
                    location_id = measurement.get("locationId")

                    if parameter and value is not None:
                        # Map OpenAQ parameter names to our standard names
                        param_mapping = {
                            "no2": "no2",
                            "o3": "o3", 
                            "pm25": "pm25",
                            "pm10": "pm10",
                            "so2": "so2",
                            "co": "co"
                        }

                        standard_param = param_mapping.get(parameter.lower())
                        if standard_param and standard_param not in pollutants:
                            pollutants[standard_param] = value
                            station_ids_used.add(str(location_id))

                return {
                    "pollutants": pollutants,
                    "station_ids": list(station_ids_used)
                }

        except httpx.RequestError as e:
            print(f"OpenAQ request error: {e}")
            return {}
        except Exception as e:
            print(f"OpenAQ service error: {e}")
            return {}

    async def get_historical_data(self, lat: float, lon: float, days: int = 7) -> List[Dict]:
        """
        Get historical air quality data for the past N days
        """
        try:
            async with httpx.AsyncClient(timeout=self.timeout) as client:
                # Calculate date range
                end_date = datetime.now()
                start_date = end_date - timedelta(days=days)

                response = await client.get(
                    f"{self.base_url}/measurements",
                    params={
                        "coordinates": f"{lat},{lon}",
                        "radius": 50000,  # 50km radius
                        "date_from": start_date.isoformat(),
                        "date_to": end_date.isoformat(),
                        "limit": 1000,
                        "order_by": "datetime",
                        "sort": "asc"
                    }
                )
                response.raise_for_status()
                data = response.json()

                # Process historical data into hourly AQI values
                historical_points = []
                measurements_by_hour = {}

                for measurement in data.get("results", []):
                    timestamp = measurement.get("date", {}).get("utc")
                    parameter = measurement.get("parameter")
                    value = measurement.get("value")

                    if timestamp and parameter and value is not None:
                        # Round to nearest hour
                        dt = datetime.fromisoformat(timestamp.replace("Z", "+00:00"))
                        hour_key = dt.replace(minute=0, second=0, microsecond=0)

                        if hour_key not in measurements_by_hour:
                            measurements_by_hour[hour_key] = {}

                        measurements_by_hour[hour_key][parameter] = value

                # Convert to AQI values for each hour
                for hour, measurements in measurements_by_hour.items():
                    # Simple AQI calculation from available measurements
                    aqi = self._calculate_simple_aqi(measurements)
                    if aqi is not None:
                        historical_points.append({
                            "t": hour.isoformat(),
                            "aqi": aqi
                        })

                return sorted(historical_points, key=lambda x: x["t"])

        except Exception as e:
            print(f"Error fetching historical data: {e}")
            return []

    def _calculate_simple_aqi(self, measurements: Dict[str, float]) -> Optional[int]:
        """Simple AQI calculation from available measurements"""
        if not measurements:
            return None

        # Simple approach: use the highest individual pollutant AQI
        max_aqi = 0

        # Basic AQI breakpoints for major pollutants
        aqi_breakpoints = {
            "pm25": [(0, 12, 0, 50), (12.1, 35.4, 51, 100), (35.5, 55.4, 101, 150)],
            "pm10": [(0, 54, 0, 50), (55, 154, 51, 100), (155, 254, 101, 150)],
            "o3": [(0, 54, 0, 50), (55, 70, 51, 100), (71, 85, 101, 150)],
            "no2": [(0, 53, 0, 50), (54, 100, 51, 100), (101, 360, 101, 150)],
        }

        for param, value in measurements.items():
            if param in aqi_breakpoints:
                for bp_low, bp_high, aqi_low, aqi_high in aqi_breakpoints[param]:
                    if bp_low <= value <= bp_high:
                        aqi = ((aqi_high - aqi_low) / (bp_high - bp_low)) * (value - bp_low) + aqi_low
                        max_aqi = max(max_aqi, int(aqi))
                        break

        return max_aqi if max_aqi > 0 else None

server/utils/geo_utils.py.content:
import math
from typing import Tuple

def format_location_name(lat: float, lon: float) -> str:
    """
    Generate a human-readable location name from coordinates
    """
    # Convert to degrees, minutes for display
    lat_deg = int(abs(lat))
    lat_min = int((abs(lat) - lat_deg) * 60)
    lat_dir = "N" if lat >= 0 else "S"

    lon_deg = int(abs(lon))
    lon_min = int((abs(lon) - lon_deg) * 60)
    lon_dir = "E" if lon >= 0 else "W"

    return f"{lat_deg}¬∞{lat_min}'{lat_dir}, {lon_deg}¬∞{lon_min}'{lon_dir}"

def calculate_distance(lat1: float, lon1: float, lat2: float, lon2: float) -> float:
    """
    Calculate the great circle distance between two points on Earth
    Returns distance in kilometers
    """
    # Convert to radians
    lat1_rad = math.radians(lat1)
    lon1_rad = math.radians(lon1)
    lat2_rad = math.radians(lat2)
    lon2_rad = math.radians(lon2)

    # Haversine formula
    dlat = lat2_rad - lat1_rad
    dlon = lon2_rad - lon1_rad

    a = (math.sin(dlat / 2) ** 2 + 
         math.cos(lat1_rad) * math.cos(lat2_rad) * math.sin(dlon / 2) ** 2)
    c = 2 * math.asin(math.sqrt(a))

    # Earth's radius in kilometers
    earth_radius = 6371.0

    return earth_radius * c

def get_region_name(lat: float, lon: float) -> str:
    """
    Get a rough region name based on coordinates
    """
    # Very simplified region mapping
    if -90 <= lat < -60:
        return "Antarctica"
    elif -60 <= lat < -30:
        if -180 <= lon < -60:
            return "South America"
        elif -60 <= lon < 20:
            return "South Atlantic"
        elif 20 <= lon < 150:
            return "Africa/Indian Ocean"
        else:
            return "Australia/Pacific"
    elif -30 <= lat < 0:
        if -180 <= lon < -30:
            return "South America"
        elif -30 <= lon < 50:
            return "Africa"
        elif 50 <= lon < 150:
            return "Asia/Australia"
        else:
            return "Pacific Ocean"
    elif 0 <= lat < 30:
        if -180 <= lon < -60:
            return "North America"
        elif -60 <= lon < 0:
            return "South America"
        elif 0 <= lon < 50:
            return "Africa/Middle East"
        elif 50 <= lon < 150:
            return "Asia"
        else:
            return "Pacific Ocean"
    elif 30 <= lat < 60:
        if -180 <= lon < -60:
            return "North America"
        elif -60 <= lon < 20:
            return "Atlantic Ocean"
        elif 20 <= lon < 150:
            return "Europe/Asia"
        else:
            return "Pacific Ocean"
    else:  # lat >= 60
        if -180 <= lon < -60:
            return "North America"
        elif -60 <= lon < 150:
            return "Arctic/Siberia"
        else:
            return "Arctic Ocean"

