import { useQuery } from "@tanstack/react-query";
import type { AirQualityResponse, SelectedLocation } from "../lib/types";

export function useAirQuality(location: SelectedLocation | null) {
  return useQuery<AirQualityResponse>({
    queryKey: ['airquality', location?.lat, location?.lon],
    queryFn: async () => {
      if (!location) throw new Error('No location provided');

      const response = await fetch(
        `/api/airquality?lat=${location.lat}&lon=${location.lon}&hours=24`
      );

      if (!response.ok) {
        throw new Error(`API Error: ${response.status} ${response.statusText}`);
      }

      return response.json();
    },
    enabled: !!location,
    staleTime: 15 * 60 * 1000, // 15 minutes to match backend cache
    refetchInterval: 15 * 60 * 1000, // Auto-refresh every 15 minutes
  });
}

client/src/hooks/useCitySearch.ts.content:
import { useQuery } from "@tanstack/react-query";
import type { CitySearchResult } from "../lib/types";

export function useCitySearch(query: string) {
  return useQuery<CitySearchResult[]>({
    queryKey: ['cities', query],
    queryFn: async () => {
      if (!query || query.length < 2) return [];

      const response = await fetch(`/api/cities/search?q=${encodeURIComponent(query)}`);

      if (!response.ok) {
        throw new Error(`Search Error: ${response.status} ${response.statusText}`);
      }

      return response.json();
    },
    enabled: query.length >= 2,
    staleTime: 5 * 60 * 1000, // 5 minutes
  });
}

client/src/lib/constants.ts.content:
export const AQI_CATEGORIES = {
  GOOD: { min: 0, max: 50, color: 'green', label: 'Good' },
  MODERATE: { min: 51, max: 100, color: 'yellow', label: 'Moderate' },
  UNHEALTHY_SENSITIVE: { min: 101, max: 150, color: 'orange', label: 'Unhealthy for Sensitive Groups' },
  UNHEALTHY: { min: 151, max: 200, color: 'red', label: 'Unhealthy' },
  VERY_UNHEALTHY: { min: 201, max: 300, color: 'purple', label: 'Very Unhealthy' },
  HAZARDOUS: { min: 301, max: 500, color: 'maroon', label: 'Hazardous' },
};

export const POLLUTANT_INFO = {
  no2: { name: 'Nitrogen Dioxide', symbol: 'NO‚ÇÇ', color: '#ff6b6b' },
  o3: { name: 'Ozone', symbol: 'O‚ÇÉ', color: '#4ecdc4' },
  pm25: { name: 'Fine Particles', symbol: 'PM2.5', color: '#45b7d1' },
  hcho: { name: 'Formaldehyde', symbol: 'HCHO', color: '#96ceb4' },
};

export const HEALTH_PROFILES = {
  general: { icon: 'üë§', label: 'General Population' },
  children: { icon: 'üë∂', label: 'Children' },
  asthma: { icon: 'ü´Å', label: 'Asthma & Respiratory' },
  elderly: { icon: 'üë¥', label: 'Elderly (65+)' },
  athletes: { icon: 'üèÉ', label: 'Athletes & Active' },
};

export function getAQICategory(aqi: number) {
  for (const [key, category] of Object.entries(AQI_CATEGORIES)) {
    if (aqi >= category.min && aqi <= category.max) {
      return { ...category, key };
    }
  }
  return AQI_CATEGORIES.HAZARDOUS;
}

export function getAQIColor(aqi: number): string {
  const category = getAQICategory(aqi);
  const colorMap: Record<string, string> = {
    green: '#10b981',
    yellow: '#f59e0b',
    orange: '#f97316',
    red: '#ef4444',
    purple: '#8b5cf6',
    maroon: '#7f1d1d',
  };
  return colorMap[category.color] || colorMap.red;
}

client/src/lib/queryClient.ts.content:
import { QueryClient, QueryFunction } from "@tanstack/react-query";

async function throwIfResNotOk(res: Response) {
  if (!res.ok) {
    const text = (await res.text()) || res.statusText;
    throw new Error(`${res.status}: ${text}`);
  }
}

export async function apiRequest(
  method: string,
  url: string,
  data?: unknown | undefined,
): Promise<Response> {
  const res = await fetch(url, {
    method,
    headers: data ? { "Content-Type": "application/json" } : {},
    body: data ? JSON.stringify(data) : undefined,
    credentials: "include",
  });

  await throwIfResNotOk(res);
  return res;
}

type UnauthorizedBehavior = "returnNull" | "throw";
export const getQueryFn: <T>(options: {
  on401: UnauthorizedBehavior;
}) => QueryFunction<T> =
  ({ on401: unauthorizedBehavior }) =>
  async ({ queryKey }) => {
    const res = await fetch(queryKey[0] as string, {
      credentials: "include",
    });

    if (unauthorizedBehavior === "returnNull" && res.status === 401) {
      return null;
    }

    await throwIfResNotOk(res);
    return await res.json();
  };

export const queryClient = new QueryClient({
  defaultOptions: {
    queries: {
      queryFn: getQueryFn({ on401: "throw" }),
      refetchInterval: false,
      refetchOnWindowFocus: false,
      staleTime: Infinity,
      retry: false,
    },
    mutations: {
      retry: false,
    },
  },
});

